con:config.script:
def testData = [ [input: "1234", expected: "one thousand two hundred and thirty four"], [input: "99", expected: "ninety nine"], [input: "10001", expected: "ten thousand one"]
]
context.setProperty("testDataList", testData) context.setProperty("index","0")

def dataList = context.getProperty("testDataList")
def index = context.getProperty("index") as int def testData = dataList[index]
// Build request XML
def soapBody = """ <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:web="http://www.dataaccess.com/webservicesserver/"> <soapenv:Header/> <soapenv:Body> <web:NumberToWords> <web:ubiNum>${testData.input}</web:ubiNum> </web:NumberToWords> </soapenv:Body> </soapenv:Envelope> """
testRunner.testCase.getTestStepByName("Request1").setPropertyValue("Request", soapBody) context.setProperty("expectedValue", testData.expected)

def result = testRunner.runTestStepByName("Request1")
def response = context.expand('${Request1#Response}')
log.info("response is : ${response}")
def expected = context.getProperty("expectedValue")

assert response.toLowerCase().contains(expected.toLowerCase()) : "Expected '${expected}' not found in response!, returned ${response}"

log.info ("Test passed for '${expected}'")

def dataList = context.getProperty("testDataList")
def index = context.getProperty("index") as int
index++

if (index < dataList.size()){
	context.setProperty("index", index.toString())
	testRunner.gotoStepByName("PrepareRequest")
}else{
	log.info ("All test cases completed.")
}
